package com.code.leetcode.code3哈希表.code2有效的字母异位词;

import java.util.*;

/**
 * @description:
 * @author: gz
 * @date: 2022/01/22
 */
public class LeetCode4_438找到字符串中所有字母异位词 {
    public static void main(String[] args) {
        //findAnagrams1：超时，失败
        //findAnagrams2：自己写，滑动窗口
        //findAnagrams3：题解，滑动窗口
        //findAnagrams2：题解，优化滑动窗口
        String s = "cbaebabacd", p = "abc";
        System.out.println(findAnagrams4(s, p));//[0,6]


        s = "abab"; p = "ab";
        System.out.println(findAnagrams4(s, p));//[0,1,2]

        s = "";
        p = "yqrbgjdwtcaxzsnifvhmou";
        System.out.println(findAnagrams4(s, p));

        s = "aa";
        p = "bb";
        System.out.println(findAnagrams4(s, p));//

        s = "baa";
        p = "aa";
        System.out.println(findAnagrams4(s, p));//1
    }

    public static List<Integer> findAnagrams1(String s, String p) {
        int bigLen = s.length();
        int smallLen = p.length();

        int slow = 0;
        int fast = smallLen;

        List<Integer> res = new ArrayList<>();
        if (bigLen < smallLen) return res;
        char[] pChars = p.toCharArray();
        Arrays.sort(pChars);
        String sortP = Arrays.toString(pChars);

        while (fast <= bigLen) {
            char[] chars = s.substring(slow, fast).toCharArray();
            Arrays.sort(chars);
            if (Arrays.toString(chars).equals(sortP)) {
                res.add(slow);
            }
            slow++;
            fast++;
        }
        return res;
    }

    public static List<Integer> findAnagrams2(String s, String p) {
        List<Integer> ans = new ArrayList<>();
        int[] record = new int[26];
        for (char c : p.toCharArray()) {
            record[c - 'a']++;
        }

        int pLen = p.length();
        if (s.length() < pLen) return ans;
        for (int i = 0; i < pLen; i++) {
            char charAt = s.charAt(i);
            record[charAt - 'a']--;
        }
        if (compare(record)) {
            ans.add(0);
        }

        for (int i = pLen; i < s.length(); i++) {
            char charAt = s.charAt(i);
            record[charAt - 'a']--;

            char charAt1 = s.charAt(i - pLen);
            record[charAt1 - 'a']++;

            if (compare(record)) {
                ans.add(i - pLen + 1);
            }
        }
        return ans;
    }

    public static boolean compare(int[] record) {
        for (int j : record) {
            if (j != 0) return false;
        }
        return true;
    }

    public static List<Integer> findAnagrams3(String s, String p) {
        int pLen = p.length();
        int sLen = s.length();

        List<Integer> ans = new ArrayList<>();
        if (sLen < pLen) return ans;
        int[] sRecord = new int[26];
        int[] pRecord = new int[26];
        for (int i = 0; i < pLen; i++) {
            sRecord[s.charAt(i) - 'a'] ++;
            pRecord[p.charAt(i) - 'a'] ++;
        }

        if (Arrays.equals(sRecord, pRecord)) {
            ans.add(0);
        }

        for (int i = 0; i < sLen - pLen; i++) {
            sRecord[s.charAt(i) - 'a']--;
            sRecord[s.charAt(i + pLen) - 'a']++;
            if (Arrays.equals(sRecord, pRecord)) {
                ans.add(i + 1);
            }
        }
        return ans;
    }

    public static List<Integer> findAnagrams4(String s, String p) {
        int sLen = s.length();
        int pLen = p.length();

        List<Integer> ans = new ArrayList<>();
        if (sLen < pLen) return ans;
        int[] record = new int[26];
        for (int i = 0; i < pLen; i++) {
            record[p.charAt(i) - 'a']--;
            record[s.charAt(i) - 'a']++;
        }
        int differ = 0;
        for (int i : record) {
            if (i != 0) differ++;
        }
        if (differ == 0) {
            ans.add(0);
        }
        for (int i = 0; i < sLen - pLen; i++) {
            if (record[s.charAt(i) - 'a'] == 1) {
                differ--;
            }
            if (record[s.charAt(i) - 'a'] == 0) {
                differ++;
            }
            record[s.charAt(i) - 'a']--;


            if (record[s.charAt(i + pLen) - 'a'] == -1) {
                differ--;
            }
            if (record[s.charAt(i + pLen) - 'a'] == 0) {
                differ++;
            }
            record[s.charAt(i + pLen) - 'a']++;

            if (differ == 0) {
                ans.add(i + 1);
            }
        }
        return ans;
    }
}
